import React from "react";
import bgImage from "../assets/images/K-210-11_c1hfol.jpg";
import {Link, Outlet, useLocation} from "react-router-dom";

const Root = () => {
    const location = useLocation();

    return (
        <div className="w-screen h-screen font-sans font-light overflow-y-hidden">
            <div className="flex space-x-8 w-full h-32 px-10 py-6">
                <p className="text-xl">VKV RESTAURATIES</p>
                <div className="flex flex-col text-xs py-1">
                    <Link to='/projects' className="hover:underline cursor-pointer">PROJECTEN</Link>
                    <Link to='/about' className="hover:underline cursor-pointer">OVER ONS</Link>
                    <Link to='/contact' className="hover:underline cursor-pointer">CONTACT</Link>
                    <Link to='/map' className="hover:underline cursor-pointer">KAART</Link>
                </div>
            </div>

            {location.pathname === '/' && (
                <div className="w-full h-full bg-black">
                    <img src={bgImage} alt="background" className="w-full h-full object-contain" />
                </div>
            )}

            <Outlet />
        </div>
    );
}

export default Root;
