import React from "react";
import { ComposableMap, Geographies, Geography } from "react-simple-maps";

const Map = () => {
    const geoUrl = "https://maps.amsterdam.nl/open_geodata/geojson_lnglat.php?KAARTLAAG=INDELING_STADSDEEL&THEMA=gebiedsindeling";
    const [tooltipContent, setTooltipContent] = useState("");

    const handleMouseEnter = (geo) => {
        const properties = geo.properties;
        // Adjust this based on your data structure; e.g., properties.name, properties.id, etc.
        setTooltipContent(properties.name || properties.description || "No details available");
    };

    const handleMouseLeave = () => {
        setTooltipContent("");
    };
    
    return (
        <div className="w-full h-full">
            <ComposableMap projection="geoMercator" className="w-full h-full" projectionConfig={{
                scale: 50000, // Increased scale for better zoom
                center: [4.9, 52.37], // Coordinates for Amsterdam
            }}>
                console.log(geographies);
                <Geographies geography={geoUrl}>
                    {({ geographies }) => {
                        console.log(geographies); // Debugging: log all geographical features
                        return geographies.map((geo) => (
                            <Geography
                                key={geo.rsmKey}
                                geography={geo}
                                style={{
                                    default: { fill: "#E4E5E6" },
                                    hover: { fill: "#F53" },
                                    pressed: { fill: "#E42" }
                                }}
                            />
                        ));
                    }}
                </Geographies>
            </ComposableMap>
        </div>
    );
}

export default Map;
