import React, {useEffect, useState} from "react";
import {useImageData} from "../ImageProvider";

const ProjectPage = () => {
    const { projectData, loading, error } = useImageData();
    const [currentProject, setCurrentProject] = useState(null);
    const [imageIndex, setImageIndex] = useState(1);

    useEffect(() => {
        const path = window.location.pathname;
        const projectName = decodeURIComponent(path.split('/').pop());
        const project = projectData.find(project => project.title === projectName);

        console.log("Project Name: " + projectName);
        console.log("Project: " + project);

        setCurrentProject(project);
    }, [projectData]);

    const getImageIndexBoxes = () => {
        const totalImages = currentProject?.images.length || 0;
        let content = [];

        for (let i = 1; i <= Math.min(4, totalImages); i++) {
            content.push(
                <div
                    key={i}
                    className={`flex justify-center items-center ${imageIndex === i ? 'bg-gray-400' : 'bg-gray-700'} 
                w-6 h-6 text-xs cursor-pointer`}
                    onClick={() => setImage(i)}
                >
                    <p>{i}</p>
                </div>
            );
        }

        if (totalImages > 4) {
            content.push(
                <div
                    key="dots"
                    className="flex justify-center items-center bg-gray-700 w-10 h-6 text-xs"
                >
                    <p>...</p>
                </div>
            );

            content.push(
                <div
                    key={totalImages}
                    className={`flex justify-center items-center ${imageIndex === totalImages ? 'bg-gray-400' : 'bg-gray-700'} 
                w-6 h-6 text-xs cursor-pointer`}
                    onClick={() => setImage(totalImages)}
                >
                    <p>{totalImages}</p>
                </div>
            );
        }

        return content;
    };

    const setImage = (index) => {
        setImageIndex(index);
    }

    return (
        <div className="flex flex-col space-y-8 justify-center items-center w-full h-full">
            <div className="w-3/4 h-3/5">
                <img src={`https://storage.googleapis.com/vkvrestauraties/${currentProject?.images[imageIndex]}`} alt="project"
                className="w-full h-full object-cover"/>
            </div>

            <div className="flex space-x-4">
                {getImageIndexBoxes()}
            </div>
        </div>
    );
}

export default ProjectPage;
