import React, { useEffect, useState } from "react";
import { useImageData } from "../ImageProvider"; // Assuming you have a custom hook

const Projects = () => {
    const { projectData, loading, error } = useImageData();
    const [currentIndex, setCurrentIndex] = useState(0);

    // Handle loading state
    if (loading) return <div>Loading...</div>;

    // Handle error state
    if (error) return <div>Error loading projects: {error}</div>;

    console.log(projectData);  // Log project data for debugging

    return (
        <div className="w-full h-full flex justify-center items-center space-x-6">
            {/* Left Arrow */}
            <svg
                xmlns="http://www.w3.org/2000/svg"
                width="50"
                height="50"
                viewBox="0 0 24 24"
                fill="none"
                stroke="white"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
                className="hover:stroke-red-500 cursor-pointer"
                onClick={() => setCurrentIndex((prevIndex) => (prevIndex - 1 + projectData.length) % projectData.length)}
            >
                <path d="M14 17l-5-5 5-5" />
            </svg>

            {/* Project Gallery */}
            <div className="w-full flex justify-center items-center space-x-6">
                {projectData.length > 0 && (
                    <div className="relative flex flex-col items-center w-1/3 h-1/2 m-4 text-white">
                        {/* Display project image */}
                        <img
                            className="object-cover w-full h-full"
                            src={`https://storage.googleapis.com/vkvrestauraties/${projectData[currentIndex]?.banner}`}
                            alt="project"
                        />
                        {/* Display project title */}
                        <p className="absolute -bottom-1/4 text-2xl">{projectData[currentIndex]?.title?.toUpperCase()}</p>
                    </div>
                )}
            </div>

            {/* Right Arrow */}
            <svg
                xmlns="http://www.w3.org/2000/svg"
                width="50"
                height="50"
                viewBox="0 0 24 24"
                fill="none"
                stroke="white"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
                className="hover:stroke-red-500 cursor-pointer"
                onClick={() => setCurrentIndex((prevIndex) => (prevIndex + 1) % projectData.length)}
            >
                <path d="M14 7l5 5-5 5" />
            </svg>
        </div>
    );
};

export default Projects;
