import React, {useEffect, useRef, useState} from "react";
import { useImageData } from '../ImageProvider';

const Home = () => {
    const { projectData, loading, error } = useImageData();
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    const images = projectData.find(folder => folder.folder === "banners")?.images || [];

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    // Effect to change the image every 3 seconds
    useEffect(() => {
        const intervalId = setInterval(() => {
            setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
        }, 3000); // Change image every 3 seconds

        // Cleanup interval on component unmount
        return () => clearInterval(intervalId);
    }, [images]);

    return (
        <div className="w-full h-full">
            <img src={`https://storage.googleapis.com/vkvrestauraties/${bannerFolder.images[1]}`}
                 alt="pic1" className="w-full h-full object-cover"/>
        </div>
    );
}

export default Home;
