import React, { useEffect, useState } from "react";
import { fetchProjects } from "../utils/ProjectUtil";
import bgImage from "../assets/images/K-210-11_c1hfol.jpg";

export default function Projects() {
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        fetchProjects().then((data) => {
            setProjects(data);
        });
    }, []);

    const cutText = (text, maxLength) => {
        if (text.length > maxLength) {
            return text.substring(0, maxLength) + '...';
        }
        return text;
    }

    return (
        <div className="flex grid-cols-4 px-10">
            {projects.map((project) => (
                <div className="relative w-1/4 h-72 group cursor-pointer">
                    <img src={bgImage} alt={project.name.toUpperCase() || ''} className="w-full h-full object-contain" />
                    <div key={project.id}
                         className="absolute inset-0 top-0 left-0 p-4 bg-opacity-25
                          w-full h-full border border-white bg-blue-400
                          text-white font-bold transition-opacity duration-300 opacity-0
                          group-hover:opacity-100 transform group-hover:translate-x-0">
                        <div className="flex flex-col justify-between w-full h-full border p-4">
                            <p className="text-xl">{project.name}</p>
                            <p className="text-xs">{cutText(project.desc, 150)}</p>
                            <p className="text-xs font-light hover:underline cursor-pointer">Lees verder</p>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}
