import React, {useEffect, useState} from "react";
import {useImageData} from "../ImageProvider";
import { Swiper, SwiperSlide } from 'swiper/react';

const Projects = () => {
    const { projectData, loading, error } = useImageData();
    const [projects, setProjects] = useState([]);

    // Filter projects on initial load to exclude 'banner'
    useEffect(() => {
        const filteredProjects = projectData.filter(project => project.title !== 'banner');
        setProjects(filteredProjects);
    }, [projectData]);

    return (
        <div className="w-full h-full flex space-x-20 justify-center items-center text-white">

            <Swiper
                spaceBetween={50}
                slidesPerView={3}
            >
                {projects.map(project => (
                    <SwiperSlide key={project.title}>
                        <img
                            src={`https://storage.googleapis.com/vkvrestauraties/${project.images[0]}`}
                            alt={project.title}
                            className="w-1/2 h-1/3 object-cover"
                        />
                    </SwiperSlide>
                ))}
            </Swiper>

        </div>
    );
}

export default Projects;
