import React, {useEffect, useState} from "react";
import {useImageData} from "../ImageProvider";
import {Spin} from "antd";

const ProjectPage = () => {
    const {projectData, loading, error} = useImageData();
    const [projectImages, setProjectImages] = useState(null);
    const [imageIndex, setImageIndex] = useState(1);

    useEffect(() => {
        const path = window.location.pathname;
        const projectName = decodeURIComponent(path.split('/').pop());
        const project = projectData.find(project => project.title === projectName);

        if (project) {
            const images = project?.images;
            const filteredImages = images.filter(image => image.split('/')[1] !== '');
            setProjectImages(filteredImages);
        }
    }, [projectData]);

    const loadProjectCircles = () => {
        if (!projectImages) return null;

        const startIndex = Math.floor(imageIndex / 3) * 3; // Start of the current group of 3
        const endIndex = Math.min(startIndex + 3, projectImages.length); // End of the current group of 3

        return projectImages.slice(startIndex, endIndex).map((_, i) => (
            <div
                key={startIndex + i}
                className={`w-4 h-4 rounded-full cursor-pointer ${
                    startIndex + i === imageIndex ? "bg-blue-500" : "bg-white opacity-65"
                }`}
                onClick={() => setImageIndex(startIndex + i)}
            />
        ));
    }

    if (loading || !projectImages || projectImages.length === 0) {
        return (
            <div className="w-full h-full flex justify-center items-center">
                <Spin size="large"/>
            </div>
        )
    }

    if (error) {
        return (
            <div className="w-full h-full flex justify-center items-center text-2xl text-red-800">
                <p>Error: {error}</p>
            </div>
        )
    }

    return (
        <div className="flex flex-col space-y-8 justify-center items-center w-full h-full">
            <div className="flex w-3/4 h-3/5 items-center">
                <svg
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 24 24"
                    fill="white"
                    width="64"
                    height="64"
                    onClick={() => setImageIndex((prev) => Math.max(prev - 1, 0))}
                    className="cursor-pointer"
                >
                    <path d="M15.41 16.59L10.83 12l4.58-4.59L14 6l-6 6 6 6z"/>
                </svg>
                <img
                    src={`https://storage.googleapis.com/vkvrestauraties/${projectImages[imageIndex - 1]}`}
                    alt="project"
                    className="w-full h-full object-cover select-none"
                    loading="lazy"
                />
                <svg
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 24 24"
                    fill="white"
                    width="64"
                    height="64"
                    onClick={() => setImageIndex((prev) => Math.min(prev + 1, projectImages.length - 1))}
                    className="cursor-pointer"
                >
                    <path d="M8.59 16.59L13.17 12l-4.58-4.59L10 6l6 6-6 6z"/>
                </svg>
            </div>
            <div className="flex space-x-2">
                {loadProjectCircles()}
            </div>
        </div>
    );
};

export default ProjectPage;
