import React, {useEffect, useState} from "react";
import {useImageData} from "../ImageProvider";

const ProjectPage = () => {
    const { projectData, loading, error } = useImageData();
    const [currentProject, setCurrentProject] = useState(null);
    const [imageIndex, setImageIndex] = useState(1);
    const [startIndex, setStartIndex] = useState(1);

    useEffect(() => {
        const path = window.location.pathname;
        const projectName = decodeURIComponent(path.split('/').pop());
        const project = projectData.find(project => project.title === projectName);

        console.log("Project Name: " + projectName);
        console.log("Project: " + project);

        setCurrentProject(project);
    }, [projectData]);

    const getImageIndexBoxes = () => {
        const totalImages = currentProject?.images.length || 0;
        let content = [];
        const maxVisible = 4; // Maximum visible buttons

        // Calculate endIndex based on startIndex and maxVisible
        const endIndex = Math.min(startIndex + maxVisible - 1, totalImages);

        for (let i = startIndex; i < endIndex; i++) {
            content.push(
                <div
                    key={i}
                    className={`flex justify-center items-center ${imageIndex === i ? 'bg-gray-400' : 'bg-gray-700'} 
                    w-6 h-6 text-xs cursor-pointer`}
                    onClick={() => handleImageClick(i, totalImages)}
                >
                    <p>{i}</p>
                </div>
            );
        }

        if (endIndex < totalImages) {
            // Add "..." and last index if there are more numbers
            content.push(
                <div
                    key="dots"
                    className="flex justify-center items-center bg-gray-700 w-6 h-6 text-xs"
                >
                    <p>...</p>
                </div>
            );
        }

        return content;
    };

    const handleImageClick = (index, totalImages) => {
        setImageIndex(index);

        // Adjust startIndex for the visible range
        if (index === startIndex + 3 && index < totalImages) {
            // Move the range up
            setStartIndex(startIndex + 1);
        } else if (index === startIndex && index > 1) {
            // Move the range down
            setStartIndex(startIndex - 1);
        }
    };

    const setImage = (index) => {
        setImageIndex(index);
    }

    return (
        <div className="flex flex-col space-y-8 justify-center items-center w-full h-full">
            <div className="w-3/4 h-3/5">
                <img src={`https://storage.googleapis.com/vkvrestauraties/${currentProject?.images[imageIndex]}`} alt="project"
                className="w-full h-full object-cover"/>
            </div>

            <div className="flex space-x-4">
                { getImageIndexBoxes() }
            </div>
        </div>
    );
}

export default ProjectPage;
