import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

const ImageContext = createContext();
const B2 = require('backblaze-b2');

const b2 = new B2({
    applicationKeyId: '8454c4075bbb', // or accountId: 'accountId'
    applicationKey: 'Master Application Key' // or masterApplicationKey
});

export const ImageProvider = ({ children }) => {
    const [images, setImages] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchImages = async () => {
            try {
                await b2.authorize(); // must authorize first (authorization lasts 24 hrs)
                let response = await b2.getBucket({ bucketName: 'vkvrestauraties' });
                setImages(response.data);
            } catch (err) {
                console.log('Error getting bucket:', err);
            } finally {
                setLoading(false);
            }
        }

        fetchImages().then(r => console.log('Images fetched:', r));
    }, []);

    return (
        <ImageContext.Provider value={{ images, loading }}>
            {children}
        </ImageContext.Provider>
    );
}

export const useImages = () => {
    return React.useContext(ImageContext);
};
