import React, { useRef, useState, useEffect} from "react";
import L from "leaflet";
import { MaptilerStyle, MaptilerLayer } from "@maptiler/sdk"; // Correct import for Maptiler SDK
import "leaflet/dist/leaflet.css";

const Map = () => {
    const mapContainer = useRef(null); // Reference to the map container
    const [map, setMap] = useState(null); // Store the map object
    const key = "CojqwWqim14N7VmgBJdJ"; // Replace with your MapTiler API key

    useEffect(() => {
        if (!map) {
            // Initialize the map if it's not already created
            const newMap = L.map(mapContainer.current).setView([50, 20], 4);

            // MapTiler Layer
            const mtLayer = MaptilerLayer({
                apiKey: key,
                style: MaptilerStyle.STREETS, // Default style
            }).addTo(newMap);

            setMap(newMap);
        }
    }, [map, key]);

    const handleStyleChange = (style) => {
        if (map) {
            const mtLayer = map._layers[Object.keys(map._layers)[0]]; // Get MapTiler layer
            mtLayer.setStyle(style); // Change the style
        }
    };

    return (
        <div className="relative w-full h-screen">
            <div
                ref={mapContainer}
                className="w-full h-full"
            ></div>

            <ul
                id="buttons"
                className="absolute top-0 left-1/2 transform -translate-x-1/2 z-10 flex space-x-4 mt-4"
            >
                <li
                    className="button px-4 py-2 bg-blue-600 text-white rounded-lg cursor-pointer font-bold"
                    onClick={() => handleStyleChange(MaptilerStyle.STREETS)}
                >
                    Streets
                </li>
                <li
                    className="button px-4 py-2 bg-blue-600 text-white rounded-lg cursor-pointer font-bold"
                    onClick={() => handleStyleChange(MaptilerStyle.OUTDOOR)}
                >
                    Outdoor
                </li>
                <li
                    className="button px-4 py-2 bg-blue-600 text-white rounded-lg cursor-pointer font-bold"
                    onClick={() => handleStyleChange(MaptilerStyle.DATAVIZ.DARK)}
                >
                    Dark
                </li>
                <li
                    className="button px-4 py-2 bg-blue-600 text-white rounded-lg cursor-pointer font-bold"
                    onClick={() => handleStyleChange(MaptilerStyle.DATAVIZ.LIGHT)}
                >
                    Light
                </li>
            </ul>
        </div>
    );
}

export default Map;
