import React, {useEffect, useState} from "react";
import {useImageData} from "../ImageProvider";
import {Link} from "react-router-dom";

const Projects = () => {
    const {projectData, loading, error} = useImageData();
    const [projects, setProjects] = useState([]);

    // Filter projects on initial load to exclude 'banner'
    useEffect(() => {
        const filteredProjects = projectData.filter(project => project.title !== 'banner');
        setProjects(filteredProjects);
    }, [projectData]);

    if (loading) {
        return (
            <div className="w-full h-full flex justify-center items-center">
                <div
                    className="inline-block h-8 w-8 animate-spin text-white rounded-full border-4 border-solid border-current
                    border-r-transparent align-[-0.125em] motion-reduce:animate-[spin_1.5s_linear_infinite]"
                    role="status">
                    <span className="!absolute !-m-px !h-px !w-px !overflow-hidden !whitespace-nowrap
                    !border-0 !p-0 ![clip:rect(0,0,0,0)]">Loading...</span>
                </div>
            </div>
        )
    }

    return (
        <div className="w-full h-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-6">
            {projects.map(project => (
                <Link to={`/project/${project.title}`} key={project.title} className="relative h-64
                group cursor-pointer overflow-hidden">
                    <img src={`https://storage.googleapis.com/vkvrestauraties/${project.banner}`} alt="project"
                         className="w-full h-full z-10 object-cover"/>
                    <div className="hidden absolute bottom-0 w-full bg-white
                    justify-center group-hover:flex animate-slide-in">
                        <p className="text-sm bottom-0 font-extrabold z-15 text-red-800">
                            {project?.title.toUpperCase() || ''}
                        </p>
                    </div>
                </Link>
            ))}
        </div>
    )
}

export default Projects;
