import React, { useEffect, useState } from "react";
import { useImageData } from '../ImageProvider';

const Home = () => {
    const { projectData, loading, error } = useImageData();
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    // Check if projectData is ready and images exist
    const images = projectData ? projectData.find(folder => folder.folder === "banners")?.images || [] : [];

    // Conditional rendering should happen here, not inside the hook
    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    // Effect to change the image every 3 seconds
    useEffect(() => {
        if (images.length === 0) return; // Prevent setting interval if images are empty

        const intervalId = setInterval(() => {
            setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
        }, 3000); // Change image every 3 seconds

        // Cleanup interval on component unmount
        return () => clearInterval(intervalId);
    }, [images.length]); // Only depend on images.length

    return (
        <div className="w-full h-full">
            {images.length > 0 && (
                <img
                    src={`https://storage.googleapis.com/vkvrestauraties/${images[currentImageIndex]}`}
                    alt="banner"
                    className="w-full h-full object-cover"
                />
            )}
        </div>
    );
};

export default Home;
