import React, { useEffect, useState } from "react";
import { fetchProjects } from "../utils/ProjectUtil";
import bgImage from "../assets/images/K-210-11_c1hfol.jpg";

export default function Projects() {
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        fetchProjects().then((data) => {
            setProjects(data);
        });
    }, []);

    const cutText = (text, maxLength) => {
        if (text.length > maxLength) {
            return text.substring(0, maxLength) + '...';
        }
        return text;
    }

    return (
        <div className="flex grid-cols-6 px-10">
            {projects.map((project) => (
                <div className="relative p-4 my-4 w-1/6 h-48">
                    <img src={bgImage} alt={project.name} className="w-full h-full object-fill" />
                    <div key={project.id}
                         className="absolute top-0 left-0 flex flex-col justify-between">
                        <p className="text-xl font-bold">{project.name}</p>
                        <p className="text-gray-600">{cutText(project.desc, 75)}</p>
                        <p className="text-xs font-light">Lees verder</p>
                    </div>
                </div>
            ))}
        </div>
    );
}
