import React, { useRef, useState, useEffect} from "react";
import {MapContainer, Marker, Popup, TileLayer} from 'react-leaflet'
import {useImageData} from "../ImageProvider";

const Map = () => {
    const { projectData, loading, error } = useImageData();
    const position = [52.368426, 4.899055]

    return (
        <div className="h-full w-full">
            <MapContainer center={position}
                          zoom={14}
                          scrollWheelZoom={true}
                          style={{width: '100%', height: '100%', zIndex: '10'}}
                          zoomControl={false}>
                <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                {projectData.map(project => (
                    <Marker key={project.id} position={[project.latitude, project.longitude]}>
                        <Popup>
                            {project.title}
                        </Popup>
                    </Marker>
                ))}
                <Marker position={position}>
                    <Popup>
                        Amsterdam, the capital city of the Netherlands.
                    </Popup>
                </Marker>
            </MapContainer>
        </div>
    );
}

export default Map;
