import React, { useRef, useState, useEffect} from "react";
import L from "leaflet";
import "@maptiler/leaflet/dist/leaflet-maptiler.css";
import { maptilerLayer, MaptilerStyle } from "@maptiler/leaflet";

const Map = () => {
    const mapContainer = useRef(null); // Reference to the map container
    const [map, setMap] = useState(null); // Store the map object
    const key = "YOUR_MAPTILER_API_KEY_HERE"; // Replace with your MapTiler API key

    useEffect(() => {
        if (!map) {
            // Initialize the map if it's not already created
            const newMap = L.map(mapContainer.current).setView([50, 20], 4);

            const mtLayer = maptilerLayer({
                apiKey: key,
                style: MaptilerStyle.STREETS, // Default style
            }).addTo(newMap);

            setMap(newMap);
        }
    }, [map, key]);
    return (
        <div className="w-full h-full flex justify-center items-center" id="map">
        </div>
    );
}

export default Map;
