import React, { useEffect, useState } from "react";
import { useImageData } from "../ImageProvider";
import {Spin} from "antd";

const ProjectPage = () => {
    const { projectData, loading, error } = useImageData();
    const [projectImages, setProjectImages] = useState(null);
    const [imageIndex, setImageIndex] = useState(1);

    useEffect(() => {
        const path = window.location.pathname;
        const projectName = decodeURIComponent(path.split('/').pop());
        const project = projectData.find(project => project.title === projectName);

        if (project) {
            const images = project?.images;
            const filteredImages = images.filter(image => image.split('/')[1] !== '');
            setProjectImages(filteredImages);
        }
    }, [projectData]);

    const handleLeftArrowClick = () => {
        if (imageIndex > 1) {
            const newIndex = imageIndex - 1;
            setImageIndex(newIndex);
        }
    };

    const handleRightArrowClick = () => {
        if (imageIndex < (projectImages ? projectImages.length : 0)) {
            const newIndex = imageIndex + 1;
            setImageIndex(newIndex);
        }
    };

    if (loading || !projectImages || projectImages.length === 0) {
        return (
            <div className="w-full h-full flex justify-center items-center">
                <Spin size="large"/>
            </div>
        )
    }

    if (error) {
        return (
            <div className="w-full h-full flex justify-center items-center text-2xl text-red-800">
                <p>Error: {error}</p>
            </div>
        )
    }

    return (
        <div className="flex flex-col space-y-8 justify-center items-center w-full h-full">
            <div className="relative w-3/4 h-3/5">
                <img
                    src={`https://storage.googleapis.com/vkvrestauraties/${projectImages[imageIndex - 1]}`}
                    alt="project"
                    className="w-full h-full object-cover select-none"
                    loading="lazy"
                />
                <div className="flex w-full h-0.5">
                    <div className="flex h-0.5 bg-gray-500 transition-all duration-300 ease-in-out"
                         style={{ width: `${(imageIndex / projectImages.length) * 100}%` }}>
                    </div>
                </div>
                <div className="absolute top-0 left-0 z-25 flex justify-between w-full h-full">
                    <div className="hover:bg-gray-400 hover:opacity-15
                     w-1/4"></div>
                    <div className="hover:bg-gray-400 hover:opacity-15
                    w-1/4"></div>
                </div>
            </div>

            <div className="flex space-x-4">
                <svg
                    onClick={handleLeftArrowClick}
                    xmlns="http://www.w3.org/2000/svg"
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="white"
                    className="hover:fill-gray-700 cursor-pointer"
                >
                    <path d="M15 19l-7-7 7-7" />
                </svg>

                {getImageIndexBoxes()}

                <svg
                    onClick={handleRightArrowClick}
                    xmlns="http://www.w3.org/2000/svg"
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="white"
                    className="hover:fill-gray-700 cursor-pointer"
                >
                    <path d="M9 5l7 7-7 7" />
                </svg>
            </div>
        </div>
    );
};

export default ProjectPage;
