import React, { useEffect, useState } from "react";
import { useImageData } from "../ImageProvider";
import { Spin } from "antd";

const ProjectPage = () => {
    const { projectData, loading, error } = useImageData();
    const [projectImages, setProjectImages] = useState(null);
    const [imageIndex, setImageIndex] = useState(1);
    const [startIndex, setStartIndex] = useState(0);
    const [direction, setDirection] = useState("forward"); // Tracks movement direction

    useEffect(() => {
        const path = window.location.pathname;
        const projectName = decodeURIComponent(path.split("/").pop());
        const project = projectData.find((project) => project.title === projectName);

        if (project) {
            const images = project?.images;
            const filteredImages = images.filter((image) => image.split("/")[1] !== "");
            setProjectImages(filteredImages);
        }
    }, [projectData]);

    const updateStartIndex = (newIndex) => {
        if (!projectImages) return;

        const totalImages = projectImages.length;
        const maxVisible = 3;

        if (newIndex === 0) {
            setStartIndex(0);
        } else if (newIndex === totalImages - 1) {
            setStartIndex(Math.max(totalImages - maxVisible, 0));
        } else {
            setStartIndex(Math.max(newIndex - 1, 0));
        }
    };

    const handleNext = () => {
        if (imageIndex < projectImages.length) {
            setDirection("forward");
            const newIndex = Math.min(imageIndex + 1, projectImages.length - 1);
            setImageIndex(newIndex);
            updateStartIndex(newIndex);
        }
    };

    const handlePrev = () => {
        if (imageIndex > 0) {
            setDirection("backward");
            const newIndex = Math.max(imageIndex - 1, 0);
            setImageIndex(newIndex);
            updateStartIndex(newIndex);
        }
    };

    const loadProjectCircles = () => {
        if (!projectImages) return null;

        const totalImages = projectImages.length;
        const endIndex = Math.min(startIndex + 3, totalImages);

        return (
            <div
                className={`flex space-x-2 transition-transform duration-300 ${
                    direction === "forward" ? "translate-x-2" : "translate-x-[-2]"
                }`}
            >
                {projectImages.slice(startIndex, endIndex).map((_, i) => {
                    const circleIndex = startIndex + i;
                    return (
                        <div
                            key={circleIndex}
                            className={`w-3 h-3 rounded-full cursor-pointer ${
                                circleIndex === imageIndex ? "bg-blue-500" : "bg-white opacity-65"
                            }`}
                            onClick={() => {
                                setDirection(circleIndex > imageIndex ? "forward" : "backward");
                                setImageIndex(circleIndex);
                                updateStartIndex(circleIndex);
                            }}
                        />
                    );
                })}
            </div>
        );
    };

    if (loading || !projectImages || projectImages.length === 0) {
        return (
            <div className="w-full h-full flex justify-center items-center">
                <Spin size="large" />
            </div>
        );
    }

    if (error) {
        return (
            <div className="w-full h-full flex justify-center items-center text-2xl text-red-800">
                <p>Error: {error}</p>
            </div>
        );
    }

    return (
        <div className="flex flex-col space-y-8 justify-center items-center w-full h-full">
            <div className="flex w-3/4 h-3/5 items-center">
                <svg
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 24 24"
                    fill="white"
                    width="64"
                    height="64"
                    onClick={handlePrev}
                    className="cursor-pointer"
                >
                    <path d="M15.41 16.59L10.83 12l4.58-4.59L14 6l-6 6 6 6z" />
                </svg>
                <img
                    src={`https://storage.googleapis.com/vkvrestauraties/${projectImages[imageIndex - 1]}`}
                    alt="project"
                    className="w-full h-full object-cover select-none"
                    loading="lazy"
                />
                <svg
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 24 24"
                    fill="white"
                    width="64"
                    height="64"
                    onClick={handleNext}
                    className="cursor-pointer"
                >
                    <path d="M8.59 16.59L13.17 12l-4.58-4.59L10 6l6 6-6 6z" />
                </svg>
            </div>
            <div className="flex overflow-hidden items-center">
                {loadProjectCircles()}
            </div>
        </div>
    );
};

export default ProjectPage;
