import React, {useEffect, useState} from "react";
import {useImageData} from "../ImageProvider";

const Projects = () => {
    const { projectData, loading, error } = useImageData();
    const [projects, setProjects] = useState([]);
    const [displayedProjectSet, setDisplayedProjectSet] = useState([]);

    // Filter projects on initial load to exclude 'banner'
    useEffect(() => {
        const filteredProjects = projectData.filter(project => project.title !== 'banner');
        setProjects(filteredProjects);
    }, [projectData]);

    // Set the initial displayed project set (first 3 projects)
    useEffect(() => {
        setDisplayedProjectSet(projects.slice(0, 3));
    }, [projects]);

    // Get the next set of projects (next 3 items)
    const nextSet = () => {
        const nextSetIndex = projects.indexOf(displayedProjectSet[displayedProjectSet.length - 1]);
        const nextSetStart = (nextSetIndex + 1) % projects.length;
        return projects.slice(nextSetStart, nextSetStart + 3);
    };

    // Get the previous set of projects (previous 3 items)
    const previousSet = () => {
        const prevSetIndex = projects.indexOf(displayedProjectSet[0]);
        const prevSetStart = (prevSetIndex - 3 + projects.length) % projects.length;
        return projects.slice(prevSetStart, prevSetStart + 3);
    };

    // Handle next button click
    const handleNext = () => {
        setDisplayedProjectSet(nextSet());
    };

    // Handle previous button click
    const handlePrevious = () => {
        setDisplayedProjectSet(previousSet());
    };

    return (
        <div className="w-full h-full flex space-x-20 justify-center items-center text-white">

            <svg xmlns="http://www.w3.org/2000/svg" width="50" height="50" viewBox="0 0 24 24" fill="none"
                 stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"
                 className="hover:stroke-red-500 cursor-pointer">
                <path d="M14 17l-5-5 5-5"></path>
            </svg>
            <div className="relative flex flex-col items-center w-1/3 h-1/2 m-4 text-white">
                <img
                    className="object-cover w-full h-full"
                    src={`https://storage.googleapis.com/vkvrestauraties/${displayedProjectSet[1].banner}`}
                    alt="project"
                />
            </div>
            <svg xmlns="http://www.w3.org/2000/svg" width="50" height="50" viewBox="0 0 24 24" fill="none"
                 stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"
                 className="hover:stroke-red-500 cursor-pointer">
                <path d="M14 7l5 5-5 5"></path>
            </svg>

        </div>
    );
}

export default Projects;
