import React, {useEffect, useState} from "react";
import {useImageData} from "../ImageProvider";

const ProjectPage = () => {
    const { projectData, loading, error } = useImageData();
    const [currentProject, setCurrentProject] = useState(null);
    const [imageIndex, setImageIndex] = useState(0);

    useEffect(() => {
        const path = window.location.pathname;
        const projectName = path.split('/').pop();
        const project = projectData.find(project => project.title === projectName);
        setCurrentProject(project);
    }, [projectData]);

    const getImageIndexBoxes = () => {
        let content = [];
        for (let i = 0; i < currentProject.images.length; i++) {
            content.push(
                <div className="flex justify-center items-center w-8 h-8 bg-gray-900">
                    <p>{i}</p>
                </div>
            );
        }
        return content;
    };

    return (
        <div className="flex flex-col space-y-4 justify-center items-center w-full h-full">
            <div className="w-3/4 h-3/5">
                <img src={`https://storage.googleapis.com/vkvrestauraties/${currentProject?.images[3]}`} alt="project"
                className="w-full h-full object-cover"/>
            </div>

            <div className="flex">
                {getImageIndexBoxes()}
            </div>
        </div>
    );
}

export default ProjectPage;
