import React, {useState} from "react";
import { ComposableMap, Geographies, Geography } from "react-simple-maps";

const Map = () => {
    const geoUrl = "https://maps.amsterdam.nl/open_geodata/geojson_lnglat.php?KAARTLAAG=INDELING_STADSDEEL&THEMA=gebiedsindeling";
    const [tooltipContent, setTooltipContent] = useState("");

    const handleMouseEnter = (geo) => {
        const properties = geo.properties;
        // Adjust this based on your data structure; e.g., properties.name, properties.id, etc.
        setTooltipContent(properties.name || properties.description || "No details available");
    };

    const handleMouseLeave = () => {
        setTooltipContent("");
    };

    return (
        <div className="w-full h-full relative">
            <ComposableMap
                projection="geoMercator"
                className="w-full h-full"
                projectionConfig={{
                    scale: 200000, // Adjust the scale for zoom
                    center: [4.9, 52.37], // Longitude and Latitude of Amsterdam
                }}
            >
                <Geographies geography={geoUrl}>
                    {({ geographies }) =>
                        geographies.map((geo) => (
                            <Geography
                                key={geo.rsmKey}
                                geography={geo}
                                onMouseEnter={() => handleMouseEnter(geo)}
                                onMouseLeave={handleMouseLeave}
                                style={{
                                    default: { fill: "#E4E5E6" },
                                    hover: { fill: "#F53" },
                                    pressed: { fill: "#E42" },
                                }}
                            />
                        ))
                    }
                </Geographies>
            </ComposableMap>
            {tooltipContent && (
                <div
                    className="absolute bg-white border shadow p-2 rounded"
                    style={{
                        top: "10px", // Adjust based on your needs
                        left: "10px", // Adjust based on your needs
                    }}
                >
                    {tooltipContent}
                </div>
            )}
        </div>
    );
}

export default Map;
