import React, {useEffect, useState} from "react";
import {useImageData} from "../ImageProvider";

const Projects = () => {
    const {projectData, loading, error} = useImageData();
    const [projects, setProjects] = useState([]);
    const [displayedProjectSet, setDisplayedProjectSet] = useState([]);

    useEffect(() => {
        const projects = projectData.filter(project => project.title !== 'banner');
        setProjects(projects);
    }, [projectData]);

    useEffect(() => {
        setDisplayedProjectSet(projects.slice(0, 3));
    }, [projects]);

    const nextSet = displayedProjectSet.map(project => {
        const index = projects.indexOf(project);
        return projects[(index + 3) % projects.length];
    });

    const previousSet = displayedProjectSet.map(project => {
        const index = projects.indexOf(project);
        return projects[(index - 3 + projects.length) % projects.length];
    });

    const handleNext = () => {
        setDisplayedProjectSet(nextSet);
    };

    const handlePrevious = () => {
        setDisplayedProjectSet(previousSet);
    }

    return (
        <div className="w-full h-full flex space-x-20 justify-center items-center text-white">

            <svg xmlns="http://www.w3.org/2000/svg" width="50" height="50" viewBox="0 0 24 24" fill="none"
                 stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"
                 className="hover:stroke-red-500 cursor-pointer">
                <path d="M14 17l-5-5 5-5"></path>
            </svg>
            {projectData.map(project => (
                project.title !== 'banner' && (
                    <div key={project.title} className="relative flex flex-col items-center w-1/3 h-1/2 m-4 text-white">
                        <img
                            className="object-cover w-full h-full"
                            src={`https://storage.googleapis.com/vkvrestauraties/${project.banner}`}
                            alt="project"
                        />

                        <p className="absolute -bottom-1/4 text-2xl">
                            {project?.title.toUpperCase() || ''}
                        </p>
                    </div>
                )
            ))}
            <svg xmlns="http://www.w3.org/2000/svg" width="50" height="50" viewBox="0 0 24 24" fill="none"
                 stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"
                 className="hover:stroke-red-500 cursor-pointer">
                <path d="M14 7l5 5-5 5"></path>
            </svg>

        </div>
    );
}

export default Projects;
